import React from "react"
const TodoItem = ({todo,onDelete}) => {
    return ( 
        <div>
            <h4>{todo.title}</h4>
            <p>{todo.desc}</p>
            <p></p>
            <button className="btn btn-danger" onClick = {()=>{onDelete(todo)}}>delete</button>
        </div>
     );
}
 
export default TodoItem;
